  @media (max-width: 800px) {
    width: 40vw;
    &:hover {
      .image {
        opacity: unset;
      }

      button {
        opacity: unset;
      }
    }
  }
`;
export const RemoveFromFavorite = styled.div`
  cursor: pointer;
  font-size: 25px;
  opacity: 0.7;
  position: absolute;
  top: 20px;
  right: 20px;
  display: none;
  color: white;

  &:hover {
    opacity: unset;
  }

  @media (max-width: 812px) {
    display: block;
    opacity: 0.9;
  }
`;

export const AddToFavoriteIcon = styled(AddToFavorite)`
  width: 10%;
  opacity: 0.7;
  position: absolute;
  top: 20px;
  right: 20px;
  display: none;
  fill: white;

  &:hover {
    opacity: unset;
  }

  @media (max-width: 812px) {
    display: block;
    opacity: 0.9;
